import React, { Component } from "react";
import Navigation from "../navigation/Navigation"
import "./resetPassword.css";
import Visibility from "@material-ui/icons/Visibility";
import IconButton from "@material-ui/core/IconButton";
import VisibilityOff from "@material-ui/icons/VisibilityOff";
import axios from "axios";
import { Link } from 'react-router-dom'
import {Button} from 'react-bootstrap';


class ResetPassword extends Component {
  constructor(props) {
    super(props);
    this.state = {
      otp: "",
      userEmail: "",
      newPassword: "",
      confirmNewPassword: "",
      otpState: "Initial Stage",
      messageStatus:"",
    };
    this.otpCreation = this.otpCreation.bind(this);
    this.handleOTP = this.handleOTP.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleNewPassword= this.handleNewPassword.bind(this);
    this.gotoLoginPage= this.gotoLoginPage.bind(this);
  }
  handleChange(event) {
    this.setState({ [event.target.name]: event.target.value });
  }
  otpCreation(event) {
    event.preventDefault();
    var userData = {
      userEmail: this.state.userEmail,
    };
    axios
          .post("/forgot-password/create-otp", userData)
          .then((response) => {
            if(response.data.msg==="OTP created") {
              this.setState({ otpState: "OTP created" ,messageStatus:"OTP Created"})
            }
            else if(response.request.responseText==="User not found") {
              this.setState({ messageStatus:"Invalid Email"})
            }
          })
            .catch(function (err) {
            });
  }
  handleOTP(event) {
    event.preventDefault();
    var userData = {
      userEmail: this.state.userEmail,
      otp: this.state.otp,
    };
    axios
          .post("/forgot-password/verify-otp", userData)
          .then((response) => {
            if(response.data.msg==="Valid OTP") {
              this.setState({ otpState: "Valid OTP" ,messageStatus:"Valid OTP"})
            }
            else if (response.data.msg==="Invalid OTP"){
              this.setState({messageStatus:"Invalid OTP"})
            }
          })
          .catch(function (err) {
            console.log(err);
          });
   }
   handleNewPassword(event){
     event.preventDefault();
    
   
     if(this.state.confirmNewPassword===this.state.newPassword)
     {var length = this.state.newPassword.length;
      if (length > 7) {
      var userData = {
        userEmail: this.state.userEmail,
        password:this.state.newPassword,
      }
      axios
          .post("/home/change-password", userData)
          .then((response) => {
          
            if(response.request.responseText==="Password changed successfully") {
              this.setState({ otpState: "" ,messageStatus:"E"})
            }
          })
          .catch(function (err) {
            console.log(err);
          });
        } else if(length<8)
        {this.setState({messageStatus:"G"})}

     }
     else if(this.state.confirmNewpassword!=this.state.newPassword){
       this.setState({messageStatus:"F"})

     }
   }
   gotoLoginPage(event) {
     event.preventDefault();
     this.props.history.push("/login")
   }

  render() {
    return (
        <React.Fragment>
        <Navigation/>
        <div className='reset-password-wrapper'>
        <form onSubmit={this.handleSignIn} className="reset-password-form-fields">
      

     
          <div className="signup-heading">Reset Password</div>   
      
      
      <div className="form-label-input-grid">
          <label>Email:</label>
            <div className="login-row-grid-input" >
            <input className="signIn-textbox"
                       required
                       type="email"
                       name="userEmail"
                       value={this.state.userEmail}
                       onChange={this.handleChange}/>
            </div>   

          </div>


            {this.state.messageStatus==="Invalid Email"&&<div className="signup-warning">User not found </div>}
            {this.state.otpState === "Initial Stage" && (
                      <Button  className="signin-button"
                      type="submit"
                      onClick={this.otpCreation }
                      > Next</Button>
                    )}
            {this.state.otpState === "OTP created" && (   
                <div>
                 <div className="form-label-input-grid">
                 <label>OTP:</label>
                   <div className="login-row-grid-input" >
                   <input className="signIn-textbox"
                              required
                              type="text"
                              name="otp"
                              value={this.state.otp}
                              onChange={this.handleChange}/>
                   </div> 
                   </div>  
                  
                   {this.state.messageStatus==="OTP created"&&<div className="signup-warning">OTP send to your email Address</div>}
                   {this.state.messageStatus==="Invalid OTP" && <div className="signup-warning">Invalid OTP </div>}

                   <Button  className="signin-button"
                     type="submit"
                     onClick={this.handleOTP}
           > Verify OTP</Button>
                 </div>
                 
            )  }   
             {this.state.otpState === "Valid OTP" && (
                 <div>
                               <div className="form-label-input-grid">
               <label>Passcode:</label>
               <div className="login-row-grid-input" >
                <input className="signIn-textbox"
                       required
                       type={this.state.showPassword ? "text" : "password"}
                       name="password"  
                       value={this.state.password}
                       onChange={this.handleChange}/>
               
               <Button className="login-page-button">
                    <IconButton className="eye-icon"
                        onClick={this.handleClickShowPassword}
                         >
                {this.state.showPassword ? <Visibility /> : <VisibilityOff />}
                    </IconButton>
                </Button>

            </div> 
            </div>

            

                </div>

             )}
            <div className="signin-alternative-options"> <Link to="/sign-page" >Go To Sign-in page</Link></div>

          

     
      <Button  className="signin-button"
           type="submit"
           > Join Room</Button>
          
</form>
</div>
        </React.Fragment>
     
    );
  }
}
export default ResetPassword;
