import React, { Component } from "react";
import axios from "axios";
import "./Signup.css";
import Navigation from "../navigation/Navigation";

class Signup extends Component {
  constructor(props) {
    super(props);
    this.state = {
      passwordMatch: true,
      passwordLength: true,
      firstName: "",
      lastName: "",
      userEmail: "",
      userRole: "",
      password: "",
      confirmPassword: "",
      status: "xyz",
    };
  }
  handleChange=(event)=> {
    this.setState({ [event.target.name]: event.target.value });
  }
  handleSubmit=(event)=> {
    event.preventDefault();
    if (this.state.password === this.state.confirmPassword) {
      var length = this.state.password.length;
      if (length > 7) {
        this.setState({ passwordLength: true, passwordMatch: true });
        var userdata = {
          firstName: this.state.firstName,
          lastName: this.state.lastName,
          userEmail: this.state.userEmail,
          userRole: "Customer",
          password: this.state.password,
        };
        console.log(userdata);
        axios
          .post("/home/signup", userdata)
          .then((response) => {
            console.log(response.request.responseText);
            this.setState({ status: response.request.responseText });
            if (response.request.responseText === "user created") {
              this.props.history.push("/login");
              this.setState({
                firstName: "",
                lastName: "",
                userEmail: "",
                password: "",
                confirmPassword: "",
              });
            }
          })
          .catch(function (err) {
            console.log(err);
          });
      } else {
        this.setState({ passwordLength: false, passwordMatch: true });
      }
    } else {
      this.setState({ passwordMatch: false });
    }
  }

  render() {
    return (
     <h1>signUp</h1>
    );
  }
}
export default Signup;
