import React,{Component} from "react";
import Navigation from "../navigation/Navigation";
import {Button} from 'react-bootstrap';
import './Login.css'
import Visibility from "@material-ui/icons/Visibility";
import IconButton from "@material-ui/core/IconButton";
import VisibilityOff from "@material-ui/icons/VisibilityOff";


class Login extends Component{
    constructor(props){
        super(props);
        this.state={
            passcode:"",    
            roomId:"",
            userName:"",
            showPassword: false,
        }
        
        this.handleChange = this.handleChange.bind(this);
        this.handleLogin = this.handleLogin.bind(this);
        this.handleClickShowPassword=this.handleClickShowPassword.bind(this)
    };
    handleChange=(event)=> {
        this.setState({ [event.target.name]: event.target.value });
      }

     handleLogin=(event)=>{
        event.preventDefault();
        var users=[];
        var roomData = {
         passcode: this.state.passcode,
         roomId: this.state.roomId
     }
     if(this.state.roomId==localStorage.getItem("roomId"))
     {
         if(this.state.passcode==localStorage.getItem("passcode")){
            this.props.history.push("/join-room")
            localStorage.setItem("userName",this.state.userName)
            users=localStorage.getItem("users")
            users = users ? users.split(',') : [];
            users.push(this.state.userName);
            localStorage.setItem("users",Jusers)
         }
         else{
             alert("invalid Password")
         }
     }
     else{
         alert("Invalid Room id")
     }
     
     
    }
     handleClickShowPassword = () => {
        this.setState({showPassword: !this.state.showPassword });
      };

    render(){
        return(
            <React.Fragment>
            <Navigation id="login-page"/>
            <form onSubmit={this.handleLogin} className="login-form-fields">
          

          <div className="form-row-grid">
              <div className="heading-create">Create Room</div>   
          </div>
          <div className="" >
          <div className="form-label-input-grid">
              <label>User Name:</label>
                <div className="login-row-grid-input" >
                <input className="login-textbox"
                           required
                           type="text"
                           name="userName"
                           value={this.state.userName}
                           onChange={this.handleChange}/>
                   
                   <Button className="login-page-button">Check</Button>
                </div>   

              </div>
              <div className="form-label-input-grid">
              <label>Room Id:</label>
                <div className="login-row-grid-input" >
                <input className="login-textbox"
                           required
                           type="text"
                           name="roomId"
                           value={this.state.roomId}
                           onChange={this.handleChange}/>
                   
                   <Button className="login-page-button">Check</Button>
                </div>   

              </div>
              <div className="form-label-input-grid">
                   <label>Passcode:</label>
                   <div className="login-row-grid-input" >
                    <input className="login-textbox"
                           required
                           type={this.state.showPassword ? "text" : "password"}
                           name="passcode"  
                           value={this.state.passcode}
                           onChange={this.handleChange}/>
                   
                   <Button className="login-page-button">
                        <IconButton className="eye-icon"
                            onClick={this.handleClickShowPassword}
                             >
                    {this.state.showPassword ? <Visibility /> : <VisibilityOff />}
                        </IconButton>
                    </Button>
                </div> 
              </div>

          </div>
          <Button  className="login-button"
               type="submit"
               > Join Room</Button>
              
   </form>
            </React.Fragment>
        )
    }
}export default Login;
